function Marqueur = RechercheForme(Im_dil3lvl)  [h,w] = size(Im_dil3lvl);  Marqueur = zeros(h,w);    %Initialisation de la reconstruction de la forme  Memo = Marqueur;          %Mémoire de la reconstruction  valeur_milieue = Im_dil3lvl(floor(h/2),floor(w/2))+0.1;  Marqueur(floor(h/2), floor(w/2)) = 1;  %Initialisation de la reconstruction  st_geod = strel('square', 20);  %Element structurant pour la reconstruction geodesique  while(norm(Marqueur - Memo) ~= 0)    %Memorisation de l'ancien marqueur    Memo = Marqueur;    %Dilatation du marqueur    Marqueur = imdilate(Marqueur, st_geod);    %Comparaison avec l'image réelle    Marqueur = (valeur_milieue*Marqueur) == Im_dil3lvl+0.1;  end    end  