function Im_dil3lvl = Seuillage3niveaux(Im_nb, Seuil_up, Seuil_down)      %Seuillage fort    Im_fort = im2bw(Im_nb,Seuil_up);    %Seuillage faible    Im_faible = im2bw(Im_nb,Seuil_down);    %Seuillage Ã  trois niveaux    Im_3lvl = (Im_fort+Im_faible)/2;    %Dilatation de l'image pour avoir des modifications franches en bordure    st_pbbord = strel('square', 20);    Im_dil3lvl = imdilate(Im_3lvl, st_pbbord);    %     figure(2)%     subplot(221)%     imshow(Im_fort)%     title('Seuillage fort')% %     subplot(222)%     imshow(Im_faible)%     title('Seuillage faible')% %     subplot(223)%     imshow(Im_3lvl)%     title('Seuillage a trois niveaux')    end